const n = 10;

// recursive solution, O(log(b))
const f1 = (a, b) => {
    const getFirstNDigits = (s, n) => s >= 10**n ? getFirstNDigits(Math.floor(s / 10), n) : s;
    // const getFirstNDigits = (a, n) => +a.toString().slice(0, n);
    const pow = (a, b) => {
        if (b === 0) return a === 0 ? NaN : 1;
        if (b === 1) return a;
        if (b === 2) return getFirstNDigits(a * a, n);
        if (b % 2 === 0) return getFirstNDigits(pow(pow(a, b / 2), 2), n);
        return getFirstNDigits(pow(pow(a, (b - 1) / 2), 2) * a, n);
    };
    return getFirstNDigits(pow(a, b), 3);
};

// iterative solution, O(b)
const f2 = (a, b) => {
    const getFirstNDigits = (s, n) => {
        while (s >= 10**n) s = Math.floor(s / 10);
        return s;
    };
    let s = 1;
    for (let i = 0; i < b; i++) {
        s = getFirstNDigits(s * a, n);
    }
    return getFirstNDigits(s, 3);
};

const [a, b] = readline().split(' ').map(Number);

// Time Test
if (0) {
