    time = Date.now();
    const s1 = f1(a, b);
    console.error('f1 takes', Date.now() - time, 'ms');// 0 ms for "Very Big" test

    time = Date.now();
    const s2 = f2(a, b);
    console.error('f2 takes', Date.now() - time, 'ms');// 439 ms for "Very Big" test

    console.error(s1 === s2);
}


