def get_number(n):
    if n == 0:
        return "INSOMNIA"

    digits = set()
    current = n
    j = 1
    while True:
        digits |= set(str(current))

        if len(digits) == 10:
            return current
        else:
            current = n * (j + 1)
        j += 1


print(get_number(int(input())))
