class Board {
	static boolean isDebug = false
	int n

	Node[] arr
	List<Set<Integer>> setList = new LinkedList<>()
	Set<Integer> wwSet = new HashSet<>()

	public Board(int n) {
		this.n = n
		arr = new Node[n]
	}

	public int reviewSetList() {
		int count = 0
		for (Set<Integer> set : setList) {
			if (set.size() <= 3) {
				count++
			} else {
				count += leaveBranchEval(set, 0)
			}
		}
		return count
	}

	// recursive
	private int leaveBranchEval(Set<Integer> set, int k) {
		for (int id : set) {
			Node nod = arr[id - 1]
			Iterator<Integer> it = nod.adj.iterator()
			while (it.hasNext()) {
				int ad = it.next()
				if (!set.contains(ad)) {
					it.remove()
				}
			}
		}

		// reset
		for (int id : set) {
			Node nod = arr[id - 1]
			nod.setToUnknown()
		}

		for (int id : set) {
			Node nod = arr[id - 1]
			if (nod.isLeaf()) {
				nod.setWhite()
				setUnknownOrWhiteAdjToBlack(nod)
			}
		}

		int countBlack = 0
		for (int id : set) {
			Node nod = arr[id - 1]
			if (nod.isBlack()) {
				countBlack++
			}
		}

		Set<Integer> unknownSet = findUnknown(set)
		if (!unknownSet.isEmpty()) {
			int recurCount = leaveBranchEval(unknownSet, k + 1)
			countBlack += recurCount
		}
		return countBlack
	}

	private Set<Integer> findUnknown(Set<Integer> set) {
		Set<Integer> uSet = new HashSet<>()
		for (int id : set) {
			Node nod = arr[id - 1]
			if (nod.isUnknown()) {
				uSet.add(id)
			}
		}
		return uSet;
	}

	public void findWhiteToWhite() {
		for (int i = 0; i < n; i++) {
			Node nod = arr[i]
			if (nod.isWhite() || nod.isUnknown()) {
				for (int j = 0; j < nod.adj.size(); j++) {
					int id2 = nod.adj.get(j)
					Node nod2 = arr[id2 - 1]
					if (nod2.isWhite() || nod2.isUnknown()) {

						wwSet.add(nod.id)
						addPair(nod.id, nod2.id)

					}
				}
			}
		}


		Set<Integer> tmpSet = null
		Set<Integer> s1 = null
		Set<Integer> s2 = null
		boolean updated = false

		for (;;) {
			updated = false

			ok:

			for (int i = 0; i < setList.size() - 1; i++) {
				for (int j = i + 1; j < setList.size(); j++) {
					Set<Integer> set1 = setList.get(i)
					Set<Integer> set2 = setList.get(j)
					for (int k : set2) {
						if (set1.contains(k)) {
							tmpSet = new HashSet<>();
							tmpSet.addAll(set1)
							tmpSet.addAll(set2)
							s1 = set1
							s2 = set2
							updated = true
							break ok
						}
					}
				}
			}

			if (!updated) {
				break
			} else {
				setList.remove(s1)
				setList.remove(s2)
				setList.add(tmpSet)
			}

		} // for ;;

	}

	private void addPair(int id1, int id2) {
		boolean done = false

		for (Set<Integer> set : setList) {
			if (set.contains(id1) || set.contains(id2)) {
				set.add(id1)
				set.add(id2)
				done = true
				break
			}
		}

		if (!done) {
			Set<Integer> set = new HashSet<>();
			set.add(id1)
			set.add(id2)
			setList.add(set)
		}

	}

	public void findWhiteWithNoBlackAdj() {
		for (int i = 0; i < n; i++) {
			Node nod = arr[i]
			if (nod.isWhite() || nod.isUnknown()) {
				if (hasNoBlackAdj(nod)) {
					nod.setBlack()
				}
			}
		}
	}

	public int countBlack() {
		if (n == 1) {
			return 1
		}
		int count = 0
		for (int i = 0; i < n; i++) {
			if (arr[i].isBlack()) {
				count++
			}
		}
		return count
	}

	public void setBlacksAdjToWhite() {
		for (int i = 0; i < n; i++) {
			if (arr[i].isBlack()) {
				for (int j = 0; j < arr[i].adj.size(); j++) {
					int id = arr[i].adj.get(j)
					if (arr[id - 1].status == 0) {
						arr[id - 1].setWhite()
					}
				}
			}
		}
	}

	private boolean hasNoBlackAdj(Node n2) {
		for (int i = 0; i < n2.adj.size(); i++) {
			int id2 = n2.adj.get(i)
			Node n22 = arr[id2 - 1]
			if (n22.isBlack()) {
				return false
			}
		}
		return true
	}

	private void setUnknownOrWhiteAdjToBlack(Node nodW) {

		for (int j = 0; j < nodW.adj.size(); j++) {
			int id = nodW.adj.get(j)
			Node n2 = arr[id - 1]

			if (n2.isUnknown() || n2.isWhite()) {
				n2.setBlack()
			}
		}
	}

	public boolean allNodesAreMarked() {
		for (int i = 0; i < n; i++) {
			if (arr[i].isUnknown()) {
				return false
			}
		}
		return true
	}

	public void setLeafWhite() {
		for (int i = 0; i < n; i++) {
			if (arr[i].isLeaf()) {
				arr[i].setWhite()
				if (!arr[i].adj.isEmpty()) {
					int nod2Id = arr[i].adj.get(0)
					arr[nod2Id - 1].setBlack()
				}
			}
		}
	}

	public void addNode(Node nod, int i) {
		arr[i] = nod
	}

	@Override
	public String toString() {

		StringBuilder sb = new StringBuilder()
		for (Node nod : arr) {
			sb.append(nod).append("\n")
		}
		return sb.toString()

	}

}


class Node {
	int id; // 1..n
	char status = 0; // 'B' Black, 'W' white,
	List<Integer> adj = new LinkedList<>()

	public Node(int id) {
		this.id = id
	}

	public void addAdj(int adjId) {
		adj.add(adjId)
	}

	public boolean isLeaf() {
		return adj.size() <= 1
	}

	public void setWhite() {
		status = 'W'
	}

	public void setBlack() {
		status = 'B'
	}

	public void setToUnknown() {
		status = 0
	}

	public boolean isWhite() {
		return status == 'W'
	}

	public boolean isBlack() {
		return status == 'B'
	}

	public boolean isUnknown() {
		return status == 0;
	}

	@Override
	public String toString() {
		char c1 = status
		if (status == 0) {
			c1 = ' '
		}
		return "Node [id=" + id + ", status=" + c1 + ", adj=" + adj + "]"
	}

}
