using System;
using System.Linq;
using System.Collections.Generic;

class Solution
{
    static void Main(string[] args)
    {
        string[] inputs;
        inputs = Console.ReadLine().Split(' ');
        int length = int.Parse(inputs[0]);
        int[,] Tab = new int[length,length];
        for (int i = 0; i < length; i++)
        {
            inputs = Console.ReadLine().Split(' ');
            for (int j = 0; j < length; j++)
            {
                int x = int.Parse(inputs[j]);
                Tab[i,j] = x;
            }
        }

        int HeartI = (length-1)/2;
        int HeartJ = (length-1)/2;

        int Heart = Tab[HeartI,HeartJ];

        int BiggestNumInFrame = -1000000;

        for(int i = 0; i < length; i++){
            int Number = Tab[0,i];
            if(Number > BiggestNumInFrame){
                BiggestNumInFrame = Number;
            }

            Number = Tab[length-1,i];
            if(Number > BiggestNumInFrame){
                BiggestNumInFrame = Number;
            }
        }

        for(int i = 0; i < length; i++){
            int Number = Tab[i,0];
            if(Number > BiggestNumInFrame){
                BiggestNumInFrame = Number;
            }

            Number = Tab[i,length-1];
            if(Number > BiggestNumInFrame){
                BiggestNumInFrame = Number;
            }
        }

        if(BiggestNumInFrame == Heart){
            Console.WriteLine("true");
        }
        else{
            Console.WriteLine("false");
        }
    }
}
