using static System.Console;
using System.Linq;

class Solution
{
    static void Main(string[] args)
    {
        int N = int.Parse(ReadLine());
        int[] sets = new int[4];
        for (int i = 0; i < N; i++)
        {
            string item = ReadLine().ToLower();
            if (item.Contains("shirt")) sets[0]++;
            if (item.Contains("jeans")) sets[1]+=2;
            if (item.Contains("sock")) sets[2]++;
            if (item.Contains("sweater")) sets[3]+=3;
        }
        sets[2] /= 2;
        Write(sets.Min());
    }
}
