import java.util.*;

class Solution {

	public static void main(String[] args) {
		final Scanner in = new Scanner(System.in);
		final int n = in.nextInt();
		final Map<String, Character> table = new HashMap<>();
		for (int i = 0; i < n; i++)
			table.put(in.next(), (char) in.nextInt());
		final String zip = in.next();

		final StringBuilder buf = new StringBuilder(), unzip = new StringBuilder();
		int i = 0;
		for (Character c; i < zip.length(); i++) {
			if ((c = table.get(buf.append(zip.charAt(i)).toString())) != null) {
				buf.delete(0, buf.length());
				unzip.append(c);
			}
		}
		System.out.println(buf.length() == 0 ? unzip : "DECODE FAIL AT INDEX " + (i - buf.length()));
	}
}
