#include <iostream>
#include <string>
#include <map>
#include <climits>

using namespace std;

static const map<char, int> ROW_DIR_CHANGE = {
    { '^', -1 },
    { 'v',  1 },
    { '<',  0 },
    { '>',  0 }
};

static const map<char, int> COL_DIR_CHANGE = {
    { '^',  0 },
    { 'v',  0 },
    { '<', -1 },
    { '>',  1 }
};

int main() {
    int W;
    int H;
    cin >> W >> H; cin.ignore();
    int startRow;
    int startCol;
    cin >> startRow >> startCol; cin.ignore();
    int N;
    cin >> N; cin.ignore();

    int shortestPath = INT_MAX;
    int shortestPathIdx = -1;
    for (int mapIdx = 0; mapIdx < N; ++mapIdx) {
        char dungeon[20][20];
        for (int rowIdx = 0; rowIdx < H; ++rowIdx) {
            string mapRow;
            getline(cin, mapRow);
            for (int colIdx = 0; colIdx < W; ++colIdx) {
                dungeon[rowIdx][colIdx] = mapRow[colIdx];
            }
        }

        int nextRowIdx = startRow;
        int nextColIdx = startCol;
        int pathLength = 0;

        while(pathLength >= 0) {
            ++pathLength;
            char& currentCell = dungeon[nextRowIdx][nextColIdx];
            if ('T' == currentCell) {
                break;
            }       

            nextRowIdx += ROW_DIR_CHANGE.at(currentCell);
            nextColIdx += COL_DIR_CHANGE.at(currentCell);

            if (nextRowIdx < 0 || nextRowIdx >= H || nextColIdx < 0 || nextColIdx >= W ||
                '.' == dungeon[nextRowIdx][nextColIdx] || '#' == dungeon[nextRowIdx][nextColIdx]
            ) {
                pathLength = -1;
            }

            currentCell = '.'; // Prevent infinite paths
        }

        if (pathLength >= 0 && pathLength < shortestPath) {
            shortestPath = pathLength;
            shortestPathIdx = mapIdx;
        }
    }

    if (-1 != shortestPathIdx) {
        cout << shortestPathIdx << endl;
    }
    else {
        cout << "TRAP" << endl;
    }
}
