// the mass of the payload to put into orbit
const P: number = parseInt(readline());
// the cost of fuel in Kg, for each Kg to lift
const C: number = parseFloat(readline());
// the number of iterations
const N: number = parseInt(readline());

// the mass computed for each iteration (at iteration 0, there is the initial payload mass)
const mass = [P];

// for each iteration, compute the fuel mass to add in order to lift the mass of the previous iteration
for (let i = 0; i < N; i++) {
    mass.push(mass[i] * C);
}

// add all the mass of the array to find the total mass
const totalMass = Math.round(mass.reduce((a, b) => a + b))
// print the result
console.log(totalMass);
