#include <stdlib.h>
#include <stdio.h>
#include <string.h>
#include <stdbool.h>
#include <time.h>

#define M 1000000007;

int main()
{
    clock_t start, end;
    double cpu_time_used;

    start = clock();

    int radius;
    scanf("%d", &radius); fgetc(stdin);
    char center[5001];
    scanf("%[^\n]", center);

    int center_length = strlen(center);
    int max_radius = 0;

    long max_points = 1;

    for (int i = 0; i < center_length; i++)
    {
        char letter = center[i];

        int dist_to_a = letter - 'a';
        int dist_to_z = 'z' - letter;

        if (dist_to_a > dist_to_z)
        {
            max_radius += dist_to_a;
        }
        else
        {
            max_radius += dist_to_z;
        }

        max_points = (max_points * 26) % M
    }

    long answer = 0;


    // Dynamic Programming

    // An array of length radius + 1 keeps track of the number of ways to make a point of
    // length 0, 1, 2, 3 ... radius - 2, radius - 1, radius. The letters of the center
    // are looked at one by one. For each letter, there are 26 options: either keep the 
    // letter the same or change it to one of the other 25 letters. Making that change
    // adds some distance to all points that have already been determined.

    if (radius < max_radius / 2)
    {
        fprintf(stderr, "\nCounting the points in the sphere:\n");

        int dp_length = 1;
        long dp[radius + 1];
        long next_dp[radius + 1];

        dp[0] = 1;
        for (int j = 1, n = radius + 1; j < n; j++)
        {
            dp[j] = 0;
            next_dp[j] = 0;
        }

        for (int i = 0; i < center_length; i++)
        {
            int idx = center[i] - 'a';

            for (int j = 0; j < dp_length; j++)
                next_dp[j] = 0;

            for (int j = -idx, n = 26 - idx; j < n; j++)
            {
                int length = j;
                if (length < 0)
                    length *= -1;

                int stop_dist = dp_length;
                if (stop_dist > radius + 1 - length)
                    stop_dist = radius + 1 - length;

                for (int dist = 0; dist < stop_dist; dist++)
                    next_dp[dist + length] = (next_dp[dist + length] + dp[dist]) % M;
            }

            if (idx > 25 - idx)
                dp_length += idx;
            else
                dp_length += 25 - idx;

            if (dp_length > radius + 1)
                dp_length = radius + 1;

            for (int k = 0; k < dp_length; k++)
                dp[k] = next_dp[k];

        }

        for (int k = 0; k < dp_length; k++)
            answer = (answer + dp[k]) % M;

    }

    // If the radius is more than 1/2 the max radius, count the points outside the sphere
    // and subtract that total from the max number of points based on the maximum radius.

    // To count the points outside the sphere requires a bit of a perspective shift. For
    // each letter in the center, there is a maximum distance a change to that letter could
    // create. It is either the distance to 'a' or the distance to 'z'. Rather than looking at 
    // how much distance is created by a change to each letter in the center, we will calculate
    // how much distance is NOT created by each possible change to each letter.

    // The distance NOT created is distance 'lost' from the maximum radius which was determined
    // earlier. Counting the points outside the sphere is a matter of finding all points that
    // lose a total distance less than or equal to the differece between the maximum possible 
    // radius and the actual radius.

    else if (radius < max_radius)
    {
        fprintf(stderr, "\nCounting the points outside the sphere:\n");

        int dp_length = 1;
        long dp[max_radius - radius];
        long next_dp[max_radius - radius];

        dp[0] = 1;
        for (int j = 1, n = max_radius - radius; j < n; j++)
        {
            dp[j] = 0;
            next_dp[j] = 0;
        }

        for (int i = 0; i < center_length; i++)
        {
            int idx = center[i] - 'a';
            int max_distance = idx;

            if (25 - idx > max_distance)
            {
                max_distance = 25 - idx;
            }

            for (int j = 0; j < dp_length; j++)
                next_dp[j] = 0;

            for (int j = -idx, n = 26 - idx; j < n; j++)
            {
                int distance_lost = max_distance - abs(j);

                for (int dist = 0; dist < dp_length; dist++)
                {
                    if (dist + distance_lost < max_radius - radius)
                    {
                        next_dp[dist + distance_lost] = (next_dp[dist + distance_lost] + dp[dist]) % M;
                    }
                }
            }

            dp_length += max_distance;

            if (dp_length > max_radius - radius)
                dp_length = max_radius - radius;

            for (int k = 0; k < dp_length; k++)
                dp[k] = next_dp[k];

        }

        for (int k = 0; k < dp_length; k++)
            answer = (answer + dp[k]) % M;

        answer = (max_points - answer) % M; 

        // Becuase of all the MOD operations, the answer could end up negative. In
        // that case, the answer needs be adjusted up by M.
        if (answer < 0)
        {
            answer += M;
        }

    }

    // # If the radius is greater than or equal to the max radius, the answer is the maximum
    // # number of point based on the maximum radius.

    else
    {
        fprintf(stderr, "\nRadius is bigger than the sphere. Printing the max points:\n");
        answer = max_points;
    }


    fprintf(stderr, "length of center = %i\n", strlen(center));
    fprintf(stderr, "max_radius = %i\n", max_radius);
    fprintf(stderr, "max_points = %li\n", max_points);

    end = clock();
    cpu_time_used = ((double) (end - start)) / CLOCKS_PER_SEC * 1000;

    fprintf(stderr, "\ntime = %f ms\n\n", cpu_time_used);

    printf("%li\n", answer);
    return 0;
}
