let n = Int(readLine()!)!
var messageParts: [Int: String] = [:]

for _ in 0...(n-1) {
    let fragment = readLine()!
    let spaceIndex = fragment.firstIndex(of: " ")!
    let order = Int(String(fragment[fragment.startIndex..<spaceIndex]))!
    let contentIndex = fragment.index(after: spaceIndex)
    let fragmentContent = String(fragment[contentIndex..<fragment.endIndex])
    messageParts[order] = fragmentContent
}

let sortedParts = messageParts.sorted(by: { $0.key < $1.key })
var isCorrupt = false
for i in 1..<sortedParts.count {
    if sortedParts[i].key - sortedParts[i - 1].key != 1 {
        isCorrupt = true
        break;
    }
}

if (isCorrupt) {
    print("CORRUPT")
} else {
    let message = sortedParts.reduce("") { $0 + $1.value }
    print(message)
}
