using System;using System;using System.Linq;using System.IO;using System.Text;using System.Collections;using System.Collections.Generic;

public class Program
{
	public static void Main()
	{		
        string phrase = Console.ReadLine();
		string kidsHearingDeficits = Console.ReadLine();

		int[] hearingDeficitArray = kidsHearingDeficits.Split(' ').Select(int.Parse).ToArray();

        var phraseList = phrase.Split(' ').ToList();

		foreach (int hdScore in hearingDeficitArray)
		{
			int firstWordNotHeard = hdScore - 1;
			for (int whichWord = firstWordNotHeard ; whichWord < phraseList.Count ; whichWord = whichWord + hdScore)
			{
                phraseList[whichWord] = "";
			}
            phraseList.RemoveAll(s => s == "");
		}

        string ans = string.Join(" ", phraseList);
        Console.WriteLine(ans);
	}	
}
