import java.util.*;

class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);

        int numberOfCurrencies = in.nextInt();
        Map<String, Double> exchangeRates = new HashMap<>();
        for (int i = 0; i < numberOfCurrencies; i++) {
            String currency = in.next();
            String exchangeRate = in.next();
            exchangeRates.put(currency, Double.valueOf(exchangeRate.substring(0, exchangeRate.length()-1)));
        }

        int numberOfTransactions = in.nextInt();
        Map<String, Double> userNetBalances = new TreeMap<>();
        for (int i = 0; i < numberOfTransactions; i++) {
            String user1 = in.next();
            String user2 = in.next();
            double amount = in.nextDouble();
            String currency = in.next();
            double amountInDollars = amount * exchangeRates.get(currency);
            userNetBalances.put(user1, userNetBalances.getOrDefault(user1, 0.0) - amountInDollars);
            userNetBalances.put(user2, userNetBalances.getOrDefault(user2, 0.0) + amountInDollars);
        }

        userNetBalances.entrySet().stream()
            .forEach(entry -> System.out.printf("%s %.2f$\n", entry.getKey(), entry.getValue()));
    }
}
