import java.io.*;
import java.math.*;

/**
 * Own more points (fishes and ice blocks) than your opponent!
 **/
class Player {
	
	public static int Fish[][] = new int[8][8];
	public static Random rand = new Random();

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int nbPlayers = in.nextInt(); // Number of players
        int playerId = in.nextInt(); // The id of the player. 0=first player, 1=second player.
        int nbPenguinsPerPlayer = in.nextInt(); // Number of penguins avalable for each player ( 4 for 2 players, 3 for 3 players, 2 for 4 players)

        // game loop
        while (true) {
            int nbRows = in.nextInt(); // number ice block grid lines
            if (in.hasNextLine()) {
                in.nextLine();
            }
            for (int i = 0; i < nbRows; i++) {
                String iceBlockLine = in.nextLine();
                
                for (int j = 0; j < iceBlockLine.length(); j++) {
			    	Fish[i][j] = (int) (iceBlockLine.charAt(j) - '0');
			    }
            }
            for (int i = 0; i < nbPlayers; i++) {
                int playerScore = in.nextInt(); // The player's score.
            }
            int nbPenguins = in.nextInt(); // The number of penguins.
            for (int i = 0; i < nbPenguins; i++) {
                int id = in.nextInt(); // The ID of the penguin.
                int ownerId = in.nextInt(); // The player ID of the penguin.
                String pos = in.next(); // The position of the penguin.
            }
            int actionCount = in.nextInt(); // number of legal actions for this turn.
            if (in.hasNextLine()) {
                in.nextLine();
            }
            
			String bestAction = null;
			int MaxScore = Integer.MIN_VALUE;
			ArrayList<String> sActionList = new ArrayList<String>();
            for (int i = 0; i < actionCount; i++) {
                String action = in.nextLine(); // the action
             
                int Score = 0;
                String[] elements = action.split(" ");
               
                int col = (int) (elements[2].charAt(0) - 'A');
                int row = (int) (elements[2].charAt(1) - '1');
           
                Score += Fish[row][col];
                
                if (action.contains("PUSH") == true) {
                	Score += 10;
                }
             
                if (Score > MaxScore) {
                	MaxScore = Score;
                	sActionList.clear();
                }
                
                if (Score == MaxScore) {
                	sActionList.add(action);
                }
            }

			int rnd = rand.nextInt(sActionList.size());
			
			// Write an action using System.out.println()
			// To debug: System.err.println("Debug messages...");
			
			System.out.println(sActionList.get(rnd)); // <move Id x y> [Optional message]
        }
    }
}

