#include <stdlib.h>
#include <stdio.h>
#include <string.h>
#include <stdbool.h>

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/

int main()
{
    int n;
    scanf("%d", &n);
    for (int i = 0; i < n; i++) {
        char to_test[51];
        scanf("%s", to_test);
        int mini =0;
        int max = 0;
        int dices = 0;
        int error = 0;
        for (int j =0; isdigit(to_test[j]); j++)
        {
            int this = 0;
            while (to_test[j]!='d')
            {
                this*=10;
                this+= to_test[j] -48;
                j++;
            }
            dices += this;
            mini +=this;
            j++;
            int value = 0;
            while (isdigit(to_test[j]))
            {
                value*=10;
                value+= to_test[j] -48;
                j++;
            }
            max += value*this;
            if (value%2)
                error++;

        }
        if (error)
        printf("Error\n");
        else if (dices >10)
        printf("TOO MANY DICE\n");
        else printf("Range %i-%i,average %i\n",dices, max, (max+dices)/2);
    }

    return 0;
}
