def get_size(crew):
    return sum(crew.values())

def str_crew(crew):
    line = '('+str(get_size(crew))+') '
    for age in sorted(crew):
        line += str(age)+':'+str(crew[age])+', '
    return line[:-2]

def simulate_flight(crew, expectancy):
    for y in range(Y):
        crew, ok = simulate_year(crew, expectancy)
        if not ok: return False
        #log('y',y,' ->',str_crew(crew))
    #log(expectancy, str_crew(crew))
    return get_size(crew)>=200

def simulate_year(crew, expectancy):
    max_fertility = expectancy//2
    nb_fertiles = 0
    new_crew = {}
    for age, nb in crew.items():
        if age>=expectancy: continue
        new_crew[age+1] = nb
        if 20<=(age+1)<=max_fertility:
            nb_fertiles += nb
    if nb_fertiles>=10:
        new_crew[0] = nb_fertiles//10
    return new_crew, get_size(new_crew)<=C


