#include<iostream>
#include <cmath>
 using namespace std;
 int main()
 {  
     int n, dist,x=0,y=0;
     char D;

     cin>>n;
     for(int i=0;i<n;i++)
     {
         cin>>D;
         switch(D)
         {
             case 'L': x--; break;
             case 'R':x++; break;
             case 'F':y++; break;
             case 'B': y--; break;
         }
     }
     dist=(x*x)+(y*y);
     dist=sqrt(dist);
     cout<<dist<<endl;

 }
