import java.util.*;
public class Solution {
	public static void main(String[] args) {
		Scanner in = new Scanner(System.in);
		String array = in.nextLine();
		String[] numsArray = array.split(" ");
		int sum=0;
		for(int i=0;i<numsArray.length;i++) {
            int num = Integer.parseInt(numsArray[i]);
			if(isPrime(num)) {
				sum += num;
			}
		}
		System.out.println(Integer.parseInt(numsArray[0]) == sum);
	}

	private static boolean isPrime(int n) {
        if(n<0)
            return false;
		for(int i=2;i<=n/2;i++) {
			if(n%i == 0) {
				return false;
			}
		}
		return true;
	}
}
