from functools import reduce
from operator import xor

bits = [int(bit) for bit in input()]
err_idx = reduce(xor, [i for i, bit in enumerate(bits) if bit])

if err_idx:
    bits[err_idx] = int(not bits[err_idx])
    if sum(bits) % 2 != 0:
        print('TWO ERRORS')
    else:
        print(''.join(str(bit) for bit in bits))
else:
    print(''.join(str(bit) for bit in bits))
