const TOTALITER = parseInt(readline());
const MAXPEOPLE = parseInt(readline());
var inside=0;
var waiting=0;
for (let i = 0; i < TOTALITER; i++) {
    var inputs = readline().split(' ');
    const INCOMINGPEOPLE = parseInt(inputs[0]);
    const EXITINGPEOPLE = parseInt(inputs[1]);
    waiting += INCOMINGPEOPLE;
    inside -= EXITINGPEOPLE;
    var capa = MAXPEOPLE - inside;
    var transfert = Math.min(capa, waiting);
    waiting -= transfert;
    inside += transfert;
    console.log(inside + ' ' + waiting);
}
