using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic;

public class Program
{
	public static string bl = " ";
	public static void Main()
	{
        int heightOfChalkboard = Int16.Parse(Console.ReadLine());
        int widthOfChalkboard = Int16.Parse(Console.ReadLine());
		string phraseBartNeedsToWriteRepeatedly = Console.ReadLine();

        int height = heightOfChalkboard;
        int positions = widthOfChalkboard;
        string phrase = phraseBartNeedsToWriteRepeatedly;

		for (int times = 0 ; times < 10 ; times++)
		{
			phrase +=  bl + phrase;
		}
		for (int lin = 1 ; lin <= height ; lin++)
		{
			phrase = PrintALine(phrase, positions);
		}
	}


	public static string PrintALine(string input, int len)
	{
		string str = input.Substring(0,len+1);
		int i = FindLocOfFirstBlankFromRight(str);
		string aLine = str.Substring(0,i).Trim();
		string blanks = "";
		int filler = len - aLine.Length;
		if (filler>0) blanks = new string(' ',filler);
		string pipe = "|";
		Console.WriteLine(pipe + aLine + blanks + pipe);
		string remaining = input.Substring(i);
		return remaining.Trim();
	}

	public static int FindLocOfFirstBlankFromRight(string aLine)
	{
		for (int pos = aLine.Length - 1 ;  pos >= 0 ; pos--)
		{
			if (aLine[pos] == ' ') return pos;
		}
		return -1;
	}
}
