const N = parseInt(readline());
let myArray = [];
for (let i = 0; i < N; i++) {
    const movie = readline();
    let title = movie.substring(0, movie.lastIndexOf("(")).trim();
    let lastPart = movie.substring(movie.lastIndexOf("(") + 1);
    let rating = lastPart.substring(0, lastPart.length - 4);

    myArray.push({
        "title": title,
        "rating": rating
    })
}

myArray.sort(function(a, b) {
    return b.rating - a.rating;
});

myArray.forEach(m => {
    if(m.rating >= 6){
        print(m.title);
    }
});
