const N = parseInt(readline());
// decrypt a character 'c'
var decrypt = (c) => {
    d = c
    if (c>='A' && c<='Z') d = String.fromCharCode(155-c.charCodeAt())
    if (c>='a' && c<='z') d = String.fromCharCode(219-c.charCodeAt())
    return d
}
// main
decryptedMessage = ''
messageLength = 0
modifiedCharactersNumber = 0
for (let i = 0; i < N; i++) {
    const message = readline();
    [...message].forEach(c => {
        messageLength++
        d = decrypt(c)
        decryptedMessage += d
        if (d!==c) modifiedCharactersNumber++   
    })
    decryptedMessage += '\n'
}

// messageLength > 0
if (modifiedCharactersNumber/messageLength >= 0.5) decryptedMessage += "Secure"
else decryptedMessage += "Insecure"

console.log(decryptedMessage)
