nbPerson, maxPerson = [int(i) for i in input().split()]
stage = int(input())
for i in range(stage):
    personOut, personIn = [int(i) for i in input().split()]
    nbPerson -= personOut
    nbPerson += personIn
if maxPerson > nbPerson:
    print("ELEVATOR", (maxPerson-nbPerson))
else:
    print("STAIRS", 0)
