#include <iostream>
#include <math.h>
#include <vector>
using namespace std;

double deg2dec(string coordinates){
    auto i1=coordinates.find("°");
    auto i2=coordinates.find("'");
    auto i3=coordinates.find("\"");
    //cerr<<coordinates<<" "<<coordinates.substr(0,i1)+"."+coordinates.substr(i1+2,i2-i1-2)+"."+coordinates.substr(i2+1,i3-i2-1)+"."+coordinates.substr(i3+1)<<endl;;
    return ((coordinates[i3+1]=='N'||coordinates[i3+1]=='E')?1:-1)
    *(stod(coordinates.substr(0,i1))+stod(coordinates.substr(i1+2,i2-i1-2))/60+stod(coordinates.substr(i2+1,i3-i2-1))/3600);
}

vector<string> collector_type={"VaCoWM Cleaner","L4nd MoWer","Cow Harvester"};
vector<double> collector_speed={44.7,22.38,11.19};//km/s
vector<double> g_param={0.8,1.2,1.5};
vector<int> collector_capacity={3,10,20};

double collector_abduction_altitude=0.5; // km
double missile_lat=deg2dec("34°45'21.8\"N");
double missile_lon=deg2dec("120°37'34.8\"W");
double missile_alt=0.046; // km
double missile_maxalt=160.; // km
double missile_speed=6.;// km/s
double g=9.81; // m/s
double PI=3.14159265359;

int main()
{
    int n;
    cin >> n; cin.ignore();
    for (int i = 0; i < n; i++) {
        string upcoming_mission;
        getline(cin, upcoming_mission);
        auto it=upcoming_mission.find(')');
        string location=upcoming_mission.substr(0,it+1);
        string coordinates=upcoming_mission.substr(it+2);
        auto it2=coordinates.find(' ');
        double lat=deg2dec(coordinates.substr(0,it2));
        string coords2=coordinates.substr(it2+1);
        auto it3=coords2.find(' ');
        double lon=deg2dec(coords2.substr(0,it3));
        double elevation=stod(coords2.substr(it3+1))/1000;//cerr<<lat<<" "<<lon<<" "<<elevation<<endl;
        double collector_altitude=elevation+collector_abduction_altitude;
        double dlat=(missile_lat-lat)*111.11;//cerr<<dlat<<endl;
        double dlon=(missile_lon-lon)*111.11*cos((lat+missile_lat)/2*PI/180);//cerr<<dlon<<endl;
        double distance=sqrt(dlat*dlat+dlon*dlon+(missile_maxalt-missile_alt)*(missile_maxalt-missile_alt));//cerr<<distance<<endl;
        double missile_impact_time = distance/missile_speed;
        vector<int> cows(3,0);//cerr<<collect_time<<" "<<abduction_time<<endl;
        for (int cs=0;cs<3;++cs) {
            double abduction_time=collector_abduction_altitude*1000/(g*g_param[cs]);
            double collect_time = missile_impact_time-(missile_maxalt-collector_altitude)/collector_speed[cs]; //missile_impact_time - fleeing_time
            while ((collect_time-abduction_time)>0) {
                ++cows[cs];
                collect_time-=abduction_time;
            }
        }
        cout<<location<<": ";
        if (cows[0]>0 || cows[1]>5 || cows[2]>13){
            cout<<"possible. Send a ";
            int t;
            if (cows[2]>13) t=2;
            else if (cows[1]>5) t=1;
            else t=0;
            cout<<collector_type[t]<<" to bring back "<<min(cows[t],collector_capacity[t])<< " cow"<<((cows[t]>1)?"s.":".")<<"\n";
        }
        else  cout<<"impossible.\n";
    }
}
