import java.util.*;
import java.io.*;
import java.math.*;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int N = in.nextInt();
        String n[]=new String[N];
        int R[]=new int[N];
        for (int i = 0; i < N; i++) {
            n[i]=in.next();
            R[i]=in.nextInt();
        }
        in.nextLine();
        String c = in.nextLine();
        for(int i=0;i<N;i++)
        c=c.replace(n[i],R[i]+"/1");
        while(c.indexOf("(")>-1||c.indexOf("[")>-1){
            if(c.lastIndexOf("[")>c.lastIndexOf("(")){
                String a[]=c.substring(c.lastIndexOf("[")+2,c.indexOf("]",c.lastIndexOf("["))).split(" ");
                int B=1;
                int T=0;
                for(int i=0;i<a.length;i++){
                    int b=Integer.parseInt(a[i].substring(0,a[i].indexOf("/")));
                    int t=Integer.parseInt(a[i].substring(a[i].indexOf("/")+1));
                    T*=b;
                    T+=t*B;
                    B*=b;
                    int A=T;
                    int D=B;
                    while(A!=0){
                        int C=A;
                        A=D%A;
                        D=C;
                    }
                    T/=D;
                    B/=D;
                }
                c=c.substring(0,c.lastIndexOf("["))+B+"/"+T+c.substring(c.indexOf("]",c.lastIndexOf("["))+1);
            }
            else{
                String a[]=c.substring(c.lastIndexOf("(")+2,c.indexOf(")",c.lastIndexOf("("))).split(" ");
                int B=1;
                int T=0;
                for(int i=0;i<a.length;i++){
                    int t=Integer.parseInt(a[i].substring(0,a[i].indexOf("/")));
                    int b=Integer.parseInt(a[i].substring(a[i].indexOf("/")+1));
                    T*=b;
                    T+=t*B;
                    B*=b;
                    int A=T;
                    int D=B;
                    while(A!=0){
                        int C=A;
                        A=D%A;
                        D=C;
                    }
                    T/=D;
                    B/=D;
                }
                c=c.substring(0,c.lastIndexOf("("))+T+"/"+B+c.substring(c.indexOf(")",c.lastIndexOf("("))+1);
            }
        }
        double r=(double)(Integer.parseInt(c.substring(0,c.indexOf("/"))))/(double)(Integer.parseInt(c.substring(c.indexOf("/")+1)));
        System.out.println(((double)java.lang.Math.round((10*r)))/10);
    }
}
