/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/

const n = parseInt(readline());
let graph = {}

// set up initial graph
for (let i = 0; i < n; i++) {
    const row = readline().split(' ');
    let first = row[0]
    let second = row[2]
    if (graph[first] === undefined) {
        graph[first] = new Set()
    }
    graph[first].add(second)
}

// complete reachable paths (not algorithmically fast, but an easy solution)
for (let i = 0; i < n; i++) {
    for (let key in graph) {
        for (succesor of graph[key]) {
            if (graph[succesor]) {
                // slow version of a set merge in JS
                graph[key] = new Set([...graph[key], ...graph[succesor]])
            }
        }
    }
}

// iff for any node holds that it can reach himself, then there is a contradiction chain
let consistent = true
for (let key in graph) {
    if (graph[key].has(key)) {
        consistent = false
    }
}

print(consistent ? "consistent" : "contradiction")
