const s = readline();
const n = parseInt(readline());

// create power set of strings
let powerSet = [''];
for (let x of s.split('')) {
    let newElements = [...powerSet].map(s => '' + s + x)
    powerSet = [...newElements, ...powerSet]
}

// sort alphabetically
powerSet.sort()

// sort by length, using sorting is stable
powerSet.sort((a,b) => a.length - b.length)

console.log(`"${powerSet[n-1]}"`);
