using System;
using System.Linq;
using System.Collections.Generic;

class Solution
{
    static void Main()
    {
        int N = int.Parse(Console.ReadLine());

        // Read data and calculate densities
        double rAlice = 0, dAlice = 0;
        var planets = new List<(string name, double temp, double dist)>();
        for (int i = 0; i < N; i++) {
            string[] inputs = Console.ReadLine().Split(' ');
            string name = inputs[0];
            double r = double.Parse(inputs[1]);
            double m = double.Parse(inputs[2]);
            double o = double.Parse(inputs[3]);

            double density = m / (4.0 / 3 * Math.PI * Math.Pow(r, 3));

            planets.Add((name, density, o));

            if (name.Equals("Alice")) {
                rAlice = r;
                dAlice = density;
            }
        }

        // Calculate Roche (replace Item2 of the tuple with density)
        planets = planets.Select(p => {
                p.temp = rAlice * Math.Pow(2 * dAlice / p.temp, 1.0/3);
                return p;
            }).ToList();

        // Sort the valid planets by distance and report the name
        Console.WriteLine(
            planets.Where(p => p.temp < p.dist)
                .OrderBy(p => p.dist)
                .First()
                .name);
    }
}

