#include <iostream>
#include <string>
#include <cmath>
#include <map>

using namespace std;

int main()
{
    map<string, uint64_t> data { {"black", 0}, {"brown", 1}, {"red", 2}, {"orange", 3}, {"yellow", 4}, {"green", 5}, {"blue", 6}, {"violet", 7}, {"grey", 8}, {"white", 9} };
    size_t N;
    string str1, str2, str3;
    cin >> N;
    for (size_t i = 0; i < N; ++i)
    {
        cin >> str1 >> str2 >> str3;
        cout << (data[str1] * 10 + data[str2]) * static_cast<uint64_t>(pow(10, data[str3])) << "\n";
    }    
    return 0;
}
