import java.util.*;

class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        long A = in.nextLong();
        long B = in.nextLong();

        System.err.println("A: " + A);
        System.err.println("B: " + B);

        long[] products = {1L,1L};
        long[] sums = {0L, 0L};

        int negative = (A < 0) ? -1 : 1;

        while (A != 0) {
            int d = (int) (A % 10);
            System.err.println("Digit="+d);
            products[0] *= d;
            sums[0] += d;
            A /= 10;
        }

        negative = (B < 0) ? -1 : 1;

        while (B != 0) {
            int d = (int) (B % 10);
            System.err.println("Digit="+d);
            products[1] *= d;
            sums[1] += d;
            B /= 10;
        }

        long sumOfProducts = products[0] + products[1];
        long productOfSums = sums[0] * sums[1];

        System.err.println("Products: " + Arrays.toString(products));
        System.err.println("Sums: " + Arrays.toString(sums));

        System.err.println("Sum of Products: " + sumOfProducts);
        System.err.println("Product of Sums: " + productOfSums);

        System.out.println(sumOfProducts > productOfSums ? "Marco" : "Polo");
    }
}
