// Example program
#include <iostream>
#include <string>
#include <iomanip>

using namespace std;

int main()
{
    double G,K,D, solution = 0.0;
    cin >> G; cin.ignore();
    for(int i = 0; i < G; i++)
    {
        cin >> K >> D; cin.ignore();
        if(D == 0) D++;
        solution += K/D;
    }
    cout << fixed;
    cout << setprecision(2);
    cout << solution/G << " " << ((solution >= 1) ? "Positive":"Negative");
}
