using System;
public class Program
{
	public static void Main()
    {
        string name = Console.ReadLine().ToUpper();
		string ans = "";
		string alphabet = "Alpha, Bravo, Charlie, Delta, Echo, Foxtrot, Golf, Hotel, India, Juliett, Kilo, Lima, Mike, November, Oscar, Papa, Quebec, Romeo, Sierra, Tango, Uniform, Victor, Whiskey, X-ray, Yankee, Zulu";
		alphabet = alphabet.Replace(" ","");

        var AlphaRay = alphabet.Split(',');

		foreach (char ch in name)
		{
			int i = (int)(ch - 'A');
			ans += AlphaRay[i] + " ";
		}
		Console.WriteLine(ans.Trim());
    }
}

