import java.util.*

fun main() {
    val input = Scanner(System.`in`)
    val ME = input.nextLine().split(" ")
    val N = input.nextInt()
    if (input.hasNextLine()) {
        input.nextLine()
    }

    val result = (0 until N).map {
        val DATE = input.nextLine().split(" ")
        DATE[0] to DATE.subList(1, DATE.size).count { it in ME }
    }.sortedByDescending { 
        it.second
    }.first().first

    println(result)
}
