using System;
using System.Collections.Generic;

class Solution
{
    static void Main(string[] args)
    {
        int n = int.Parse(Console.ReadLine());

        List<string[]> suspiciousLogs = new List<string[]>();
        for (int i = 0; i < n; i++)
        {
            string[] log = Console.ReadLine().Split();

            string time = log[0];
            int hour = Int32.Parse(time.Split(':')[0]);
            // The museum was open, so this can't be the thief
            if (hour < 20 && hour > 8)
                continue;

            // This log has a timestamp during closed hours, so it's suspicious
            suspiciousLogs.Add(log);
        }

        string thiefID = "";
        // Check pairs of the suspicious logs
        for(int i = 0; i < suspiciousLogs.Count - 1; i++)
        {
            // Check that the same employee closed the case after opening it
            string[] log = suspiciousLogs[i];
            string[] nextLog = suspiciousLogs[i + 1];

            // The case was opened, then closed...
            if (log[2] == "1" && nextLog[2] == "0")
            {
                // But by different people!
                if (log[1] != nextLog[1])
                {
                    // So the thief must be the one who opened it
                    thiefID = log[1];
                    break;
                }
            }
        }

        // If we couldn't find the thief among pairs of logs, they must be in the last log entry
        if (thiefID == "")
        {
            thiefID = suspiciousLogs[suspiciousLogs.Count - 1][1];
        }

        Console.WriteLine(thiefID);
    }
}
