#define NBLL 4

typedef struct {
    uint64 p[NBLL];
} Board;

Board zero={{0x0ULL,0x0ULL,0x0ULL,0x0ULL}};

Board util;

int computeP(int x, int y)
{
    return (y+1)/4;
}

int computeB(int x, int y)
{
    return ((y+1)%4)*16+x;
}

Board upBoard(Board b)
{
    uint64 v;
    Board r;
    r.p[0]=b.p[0]>>16;
    r.p[0]^=(r.p[0]&0xffffULL);
    v=b.p[1]&0xffffULL;
    r.p[0]|=(v<<48);
    r.p[1]=b.p[1]>>16;
    v=b.p[2]&0xffffULL;
    r.p[1]|=(v<<48);
    r.p[2]=b.p[2]>>16;
    v=b.p[3]&0xffffULL;
    r.p[2]|=(v<<48);
    r.p[3]=b.p[3]>>16;
    return r;
}

Board downBoard(Board b)
{
    Board r;
    r.p[3]=b.p[3]&0xffffULL;
    r.p[3]=(r.p[3]<<16)|(b.p[2]>>48);
    r.p[2]=(b.p[2]<<16)|(b.p[1]>>48);
    r.p[1]=(b.p[1]<<16)|(b.p[0]>>48);
    r.p[0]=(b.p[0]<<16);
    return r;
}

Board andBoard(Board b1, Board b2)
{
    Board r=b1;
    r.p[0]&=b2.p[0];
    r.p[1]&=b2.p[1];
    r.p[2]&=b2.p[2];
    r.p[3]&=b2.p[3];
    return r;
}

Board orBoard(Board b1, Board b2)
{
    Board r=b1;
    r.p[0]|=b2.p[0];
    r.p[1]|=b2.p[1];
    r.p[2]|=b2.p[2];
    r.p[3]|=b2.p[3];
    return r;
}

Board xorBoard(Board b1, Board b2)
{
    Board r=b1;
    r.p[0]^=b2.p[0];
    r.p[1]^=b2.p[1];
    r.p[2]^=b2.p[2];
    r.p[3]^=b2.p[3];
    return r;
}

Board leftBoard(Board b)
{
    Board r;
    r.p[0]=(b.p[0]>>1);
    r.p[1]=(b.p[1]>>1);
    r.p[2]=(b.p[2]>>1);
    r.p[3]=(b.p[3]>>1);
    return andBoard(r,util);
}

Board rightBoard(Board b)
{
    Board r;
    r.p[0]=(b.p[0]<<1);
    r.p[1]=(b.p[1]<<1);
    r.p[2]=(b.p[2]<<1);
    r.p[3]=(b.p[3]<<1);
    return andBoard(r,util);
}

int countBoard(Board b)
{
    return bit_count(b.p[0])+bit_count(b.p[1])+bit_count(b.p[2])+bit_count(b.p[3]);
}

void firstBoard(Board b, int *x, int *y)
{
    *x=-1;
    *y=-1;
    if (b.p[0]!=0ULL)
    {
        int a=bit_first(b.p[0]);
        *x=a%16;
        *y=((a/16)-1);
    }
    else if (b.p[1]!=0ULL)
    {
        int a=bit_first(b.p[1]);
        *x=a%16;
        *y=((a/16)+3);
    }
    else if (b.p[2]!=0ULL)
    {
        int a=bit_first(b.p[2]);
        *x=a%16;
        *y=((a/16)+7);
    }
    else if (b.p[3]!=0ULL)
    {
        int a=bit_first(b.p[3]);
        *x=a%16;
        *y=((a/16)+11);
    }
}

Board restBoard(Board b)
{
    Board r=b;
    if (b.p[0]!=0ULL)
    {
        r.p[0]=bit_rest(b.p[0]);
    }
    else if (b.p[1]!=0ULL)
    {
        r.p[1]=bit_rest(b.p[1]);
    }
    else if (b.p[2]!=0ULL)
    {
        r.p[2]=bit_rest(b.p[2]);
    }
    else if (b.p[3]!=0ULL)
    {
        r.p[3]=bit_rest(b.p[3]);
    }
    return r;
}

Board setInBoard(Board b, int x, int y)
{
    Board r=b;
    int indp=computeP(x,y);
    int indb=computeB(x,y);
    r.p[indp]|=(1ULL<<indb);
    return r;
}

