void movelistSort(Movelist * ml)
{
   // init
   int size = ml->nbm;
   assert(size<MAXMOVE);
   if (size <= 1) return;

   ml->score[size] = -(1 << 30); // HACK: sentinel

   // insert sort (stable)

   for (int i = size - 2; i >= 0; i--) {

      Move mv = ml->mv[i];
      int  sc = ml->score[i];

      int j;

      for (j = i; sc < ml->score[j + 1]; j++) {
         ml->mv[j]  = ml->mv[j + 1];
         ml->score[j] = ml->score[j + 1];
      }

      assert(j < size);

      ml->mv[j]  = mv;
      ml->score[j] = sc;
   }

   // debug

#ifndef NDEBUG
      for (int i = 0; i < size - 1; i++) {
         assert(ml->score[i] >= ml->score[i + 1]);
      }
#endif

}

