            for (int i = 0; i < validActionCount; i++) {
                int col = in.nextInt();
                int row = in.nextInt();
                String shape = in.next();
                //System.err.println(col+" "+row+" "+shape);
                int v=val[shape.charAt(0)-65];
                if (v>max)
                {
                	max=v;
                	mrow=row;
                	mcol=col;
                	mshape=shape;
                }
            }

            System.out.println(String.format("%d %d", mcol, mrow)+" "+mshape);
        }
    }
