import 'dart:io';
import 'dart:math';

void main() {
    List inputs;
    int n = int.parse(stdin.readLineSync());
    inputs = stdin.readLineSync().split(' ');
    var values = [];
    for (int i = 0; i < n; i++) {
        int val = int.parse(inputs[i]);
        values.add(val);
    }

    while (values.length > 1) {
        var newList = [];
        for (int i = 0; i < values.length - 1; i++) {
            newList.add(values[i] + values[i+1]);
        }
        values.clear();
        for (int i = 0; i < newList.length; i++) {
            values.add(newList[i]);
        }
    }
    print(values[0]);
}
