const d = parseInt(readline());
const m = parseInt(readline());
const inputs = readline().split(' ');

let max = 0;
let maxIndex = 0;
for (let i = 0; i < m - d + 1; i++) {
    let localSum = 0;
    for (let j = 0; j < d; j++) {
        localSum += parseInt(inputs[i+j]);
    }
    if(localSum > max) {
        max = localSum;
        maxIndex = i;
    }
}

let solution = [];
for (let i = 0; i < d; i++) {
    solution.push(maxIndex+i+1);
}

console.log(solution.join(' '));
