const names = []
const quantities = []

for (let i = 0; i < 3; i++) {
    const line = readline().split(' ');
    names.push(line[0])
    quantities.push(parseInt(line[1]))
}

// look for the smallest quantity

const min = Math.min(...quantities)

// if there is more than one minimum, take the first one that is not chickens

const tie = quantities.filter(q => q === min).length > 1

const minIndex = tie
    ? quantities.findIndex((q, i) => q === min && i > 0)
    : quantities.findIndex((q) => q === min)

// the winner is the next one

winnerIndex = (minIndex + 1) % 3

const winnerName = names[winnerIndex]

// substract the smallest quantity from the winner's quantity, minus 1 if the smallest is not chickens

let quantity = quantities[winnerIndex] - quantities[minIndex]

if (minIndex !== 0) {
    quantity++
}

console.log(`${winnerName} ${quantity}`);


