using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic;

public class Program
{
	public static int meLin;
	public static int mePos;

	public static void Main()
	{
        int heightOfPlayground  = int.Parse(Console.ReadLine());
        int HI = heightOfPlayground;

        var TaggersList = new List<Tagger>();

        for (int lin = 0 ; lin < HI ; lin++)
        {
            string ROW = Console.ReadLine();
            string aLine = ROW;
            int WI = aLine.Length;
            for (int pos = 0; pos < WI ; pos++)
            {
                char ch = aLine[pos];
                if (ch == '*')
                {
                    meLin = lin;
                    mePos = pos;
                }
                else if (ch != ' ')
                {
                    Tagger temp = new Tagger(lin,pos,ch);
                    TaggersList.Add(temp);
                }
            }
        }

		foreach (Tagger tgr in TaggersList)
		{
			tgr.CalcDistToMe();
		}

		List<Tagger> SortedList = TaggersList.OrderBy(o=>o.DistToMe).ToList();

        string ans = "" + SortedList[0].Name;
		Console.WriteLine(ans);
	}


	public class Tagger	
	{
		public int Lin;
		public int Pos;
		public char Name;
		public double DistToMe;

		public Tagger(int lin, int pos, char ch)
		{
			Lin = lin;
			Pos = pos;
			Name = ch;
		}
		public void CalcDistToMe()
		{
			int a = meLin - Lin;
			int b = mePos - Pos;
			double aa = a * a;
			double bb = b * b;
			double cc = Math.Sqrt(aa + bb); //cc = Math.Round(cc,2);
			DistToMe = cc;
		}
	}
}
