using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic;
/*

*/

public class Program
{	
	public static string str0 = "Adam Alex Archie Arnold August Bert Calvin Charlie Clifton Cornelius Curtis Dan Dennis Dewey Douglas Earnest Ed Elbert Ellis Felix Franklin Gerald Gilbert Glenn Gordon Horace Hubert Ira Irvin Irving Jake Jerome Jerry Jose Juan Mack Marion Marshall Maurice Max Morris Nathan Nathaniel Nelson Oliver Ollie Orville Otis Otto Percy Perry Pete Roland Roosevelt Roscoe Rudolph Rufus Sidney Stepback Sylvester Ted Vernon Vincent Virgil Wallace Warren Wayne Wesley Wilbur Willard";
    public static string str1 = "Amber Aqua Aquamarine Auburn Azure Beige Black Blue Bronze Brown Buff Cerulean Charcoal Chartreuse Chocolate Copper Coral Crimson Cyan Ecru Emerald Fuchsia Gold Goldenrod Gray Green Indigo Ivory Jade Khaki Lavender Lilac Magenta Maroon Mauve Olive Orange Orchid Periwinkle Pewter Pink Puce Purple Red Rose Ruby Sapphire Scarlet Sepia Sienna Silver Tan Taupe Teal Terracotta Topaz Turquoise Ultramarine Umber Vermilion Violet Viridian White Yellow";
    public static string str2 = "Alligator Armadillo Baboon Badger Bat Bear Beaver Buffalo Bull Camel Cat Cheetah Chimpanzee Cow Crocodile Crow Dog Dolphin Donkey Duck Elephant Fish Fox Frog Giraffe Goat Goose Gorilla Hamster Hedgehog Hen Hippopotamus Horse Iguana Jaguar Jellyfish Kangaroo Koala Lemur Lion Lizard Llama Mammoth Meerkat Monkey Mouse Orangutan Ostrich Otter Owl Panther Pigeon Rabbit Racoon Rat Rhinoceros Sheep Sloth Snake Swan Tiger Turtle Whale Wolf";
	public static string[] Ray0, Ray1, Ray2;

	public static void Main()
	{
		Ray0 = str0.Split(' ');
		Ray1 = str1.Split(' ');
		Ray2 = str2.Split(' ');

        string[] inputs = Console.ReadLine().Split(' ');
        string initials = inputs[0];
        int minimumLength = int.Parse(inputs[1]);
		int maximumLength = int.Parse(inputs[3]);

        int mini = minimumLength;
        int maxi = maximumLength;

		List<string> answerList = new List<string>();

        answerList = GetAnswersList(initials, mini, maxi);
        answerList.Sort();

        if (answerList.Count == 0) Console.WriteLine("NONE"); 
		foreach (var item in answerList) Console.WriteLine(item); 
    }

	public static List<string> GetAnswersList(string theInitials, int mini, int maxi)
	{
		var outputList = new List<string>();
		var subList0 = MakeSublist(Ray0, theInitials[0]);
		var subList1 = MakeSublist(Ray1, theInitials[1]);
		var subList2 = MakeSublist(Ray2, theInitials[2]);

		foreach (var s0 in subList0)
		{
			foreach (var s1 in subList1)
			{
				foreach (var s2 in subList2)
				{
					string fullName = $"{s0} the {s1} {s2}";
					int len = fullName.Length;
					if (len >= mini && len <= maxi)
					{
						outputList.Add(fullName);
					}
				}
			}
		}
		return outputList;
	}

	public static List<string> MakeSublist(string[] anArray, char aLetter)
	{
		var list = anArray.Where(x => x[0] == aLetter);
		return list.ToList();
	}
}


