#include <iostream>
#include <vector>
#include <sstream>
#include <iterator>

bool isFullCycle(std::vector<int> V) {
    int              idx(0), cmpt(0);
    std::vector<int> vis(V.size(), 0);

    while ( vis.at(idx) == 0 )
    {
        vis[idx] = 1;
        cmpt++;
        idx = ( idx + V[idx] )  % (int)V.size();
        if ( idx < 0 ) idx += (int)V.size();
    }

  return !idx && cmpt == V.size();
}

int main()
{
    int N; std::cin >> N;
    std::istream_iterator<int> it(std::cin), eos;
    std::vector<int>           V (it, eos );

    std::cout << ( isFullCycle(V) ? "True" : "False" ) << std::endl;
}
