data.solution:
players=input().split()
game=[]
for i in range(3):
    game.append([*input()])

for player in players:
    for line in game:
        if(''.join(line)== player*3):
            print('Player',players.index(player)+1)
            quit()

    for i in range(3):
        if(''.join([game[j][i] for j in range(3)])==player*3):
            print('Player',players.index(player)+1)
            quit()
    if(''.join([game[i][i] for i in range(3)])==player*3 or ''.join([game[2-i][i] for i in range(3)])==player*3):
        print('Player',players.index(player)+1)
        quit()

for line in game:
    if '.' in line:
        print('Incomplete')
        quit()
print('Draw')
