using System;
using System.Linq;
using System.Collections.Generic;

class Solution
{
    static void Main(string[] args)
    {
        int N = int.Parse(Console.ReadLine());
        List<int> buildings = new List<int>();
        List<string> sunsetViewer = new List<string>();

        for (int i = 0; i < N; i++)
        {
            buildings.Add(int.Parse(Console.ReadLine()));
        }

        for (int i = 0; i < N; i++)
        {
            bool hasSunsetView = true;
            for (int j = i + 1; j < N; j++)
            {
                if (buildings[j] >= buildings[i])
                {
                    hasSunsetView = false;
                }
            }

            if (hasSunsetView)
            {
                sunsetViewer.Add($"{buildings[i]} [{i}]");
            }
        }

        Console.WriteLine(string.Join(" ", sunsetViewer));
    }
}
