const X = parseInt(readline());
const Y = parseInt(readline());
const Z = parseInt(readline());

function lcm(x, y) {
   if ((typeof x !== 'number') || (typeof y !== 'number')) 
    return false;
  return (!x || !y) ? 0 : (x * y / gcd(x, y));
}

function gcd(x, y) {
  while(y) {
    var t = y;
    y = x % y;
    x = t;
  }
  return x;
}

l=lcm(lcm(X,Y),Z)
s=(l/X)+(l/Y)+(l/Z)

