using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic;

public class Program
{	
	public static Dictionary<int,DigitModel> dic = new Dictionary<int,DigitModel>();
	public static char[,] InputRay;
    public const char plus = '+';
	public const char blank = ' ';

	public static void Main()
	{
		Load10Digits();

		var InputList = new List<string>();
		for (int whichRow = 0 ; whichRow < 23 ; whichRow++)
		{
            string temp = Console.ReadLine();
			InputList.Add(temp);	
		}

        InputRay = new char[23,17];
		for (int lin = 0; lin < 23 ; lin++)
		{
            string thisInputLine = InputList[lin];
            if (thisInputLine.Length < 17)
            {
                thisInputLine = new string(' ', 17); // dont need words "add" and "subtract"
            }
			for (int pos = 0; pos < 17; pos++)
			{
				InputRay[lin,pos] = thisInputLine[pos];
			}
		}

		/*
		1 2 
		3 4
		5 6		
		*/

		string pat1 = FindPatternFromInput(1);
		string pat2 = FindPatternFromInput(2);
		string pat3 = FindPatternFromInput(3);
		string pat4 = FindPatternFromInput(4);
		string pat5 = FindPatternFromInput(5);
		string pat6 = FindPatternFromInput(6);
		string pat7 = "";
		string pat8 = "";

        bool boo;
		for (int i = 0 ; i < 7 ; i++)
		{
			char ch1 = pat1[i];
			char ch3 = pat3[i];
			char ch5 = pat5[i];
			boo = (ch1 == plus && ch3 != plus) || ch5 == plus;
			char ch7 = boo ? plus : blank;
			pat7 += ch7;

			char ch2 = pat2[i];
			char ch4 = pat4[i];
			char ch6 = pat6[i];
			boo = (ch2 == plus && ch4 != plus) || ch6 == plus;
			char ch8 = boo ? plus : blank;
			pat8 += ch8;
		}

		string ANS = FindIntWithThisPattern(pat7) + "" + FindIntWithThisPattern(pat8) ;
		Console.WriteLine(ANS);
	}

	public class DigitModel
	{
		public string Pattern;
		public DigitModel (string pattern)
		{
			Pattern = pattern;
		}
	}

	public static string FindPatternFromInput(int whichDigit)
	{
		string pattern = "";
		int startingLin = -1 ;
		int startingPos = -1 ;//
		if (whichDigit == 1) {startingLin = 1; startingPos = 2;}
		if (whichDigit == 2) {startingLin = 1; startingPos = 10; }
		if (whichDigit == 3) {startingLin = 9; startingPos = 2 ;}
		if (whichDigit == 4) {startingLin = 9; startingPos = 10 ;}
		if (whichDigit == 5) {startingLin = 17; startingPos = 2 ;}
		if (whichDigit == 6) {startingLin = 17; startingPos = 10 ;}

		pattern += InputRay[startingLin,     startingPos + 1 ] == ' ' ? ' ' : '+';
		pattern += InputRay[startingLin + 1, startingPos     ] == ' ' ? ' ' : '+';
		pattern += InputRay[startingLin + 1, startingPos + 4 ] == ' ' ? ' ' : '+';

		pattern += InputRay[startingLin + 2, startingPos + 1 ] == ' ' ? ' ' : '+';
		pattern += InputRay[startingLin + 3, startingPos     ] == ' ' ? ' ' : '+';
		pattern += InputRay[startingLin + 3, startingPos + 4 ] == ' ' ? ' ' : '+';

		pattern += InputRay[startingLin + 4, startingPos + 1 ] == ' ' ? ' ' : '+';

		return pattern;
	}

	public static int FindIntWithThisPattern(string pattern)
	{
		foreach (var kvp in dic)
		{
			if (kvp.Value.Pattern == pattern)	
				return kvp.Key;
		}
		return -101;
	}

	public static void Load10Digits()
	{	
		dic.Add(8, new DigitModel("+++++++"));
		dic.Add(0, new DigitModel("+++ +++"));
		dic.Add(1, new DigitModel("  +  + "));
		dic.Add(2, new DigitModel("+ +++ +"));
		dic.Add(3, new DigitModel("+ ++ ++"));
		dic.Add(4, new DigitModel(" +++ + "));
		dic.Add(5, new DigitModel("++ + ++"));
		dic.Add(6, new DigitModel("++ ++++"));
		dic.Add(7, new DigitModel("+ +  + "));
		dic.Add(9, new DigitModel("++++ ++"));
		//dic.Add(-1, new DigitModel("       ")); // a blank
	}
}
