#include <stdio.h>
#include <stdlib.h>
#include <string.h>

int BinToDec(char bin[]);


int main(void) {

//read Number of Lines
int N;
scanf("%d", &N);
//read Checksum
int C;
scanf("%d", &C);

//create string to store output
char string[2048];
string[N*2]='\0';

//read next N lines
for(int i=0; i<N*2; i+=2) {

  char bin0[11], bin1[11], checks[11];
  scanf("%s %s %s", bin0, bin1, checks);

  //convert to integer and add value to string - using Checksum
  int bin0i=BinToDec(bin0),bin1i=BinToDec(bin1), checksi = BinToDec(checks);

  //find broken transmission (transmission that is either 0b11111111 or 0b00000000)
  if(bin0i==0b11111111||bin0i==0b00000000) {bin0i= bin1i^checksi^C;}
  else if(bin1i==0b11111111||bin1i==0b00000000) {bin1i= bin0i^checksi^C;}

  string[i]=bin0i;
  string[i+1]=bin1i;


}

//ouput string
printf("%s\n", string);

return 0;
}


int BinToDec(char bin[]) {

  int val=0;

  for(int i=0; i<8; i++) {
    val |= bin[9-i]=='1'?(1<<i):0;
  }
  return val;
}
