c, n = [int(i) for i in input().split()]
boxes = []
for i in range(n):
    price, paperclips = [int(j) for j in input().split()]
    boxes.append((price, paperclips))
best = 0
for i in range(2**n):
    bitstring = bin(i)[2:].zfill(n)
    price = paperclips = 0
    for bit, box in zip(bitstring, boxes):
        if bit == '1':
            price += box[0]
            paperclips += box[1]
    if price <= c:
        best = max(best, paperclips)
print(best)
